import React, { useState } from "react";

interface InventoryItem {
  name: string;
  quantity: number;
}

const InventoryManagement: React.FC = () => {
  const [items, setItems] = useState<InventoryItem[]>([]);
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState<number>(0);
  const [editIndex, setEditIndex] = useState<number | null>(null);

  const handleAddOrEdit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!name.trim() || quantity <= 0) return;

    if (editIndex !== null) {
      // Edit existing item
      const updated = [...items];
      updated[editIndex] = { name: name.trim(), quantity };
      setItems(updated);
      setEditIndex(null);
    } else {
      // Add new item
      setItems([...items, { name: name.trim(), quantity }]);
    }
    setName("");
    setQuantity(0);
  };

  const handleEdit = (idx: number) => {
    setEditIndex(idx);
    setName(items[idx].name);
    setQuantity(items[idx].quantity);
  };

  const handleDelete = (idx: number) => {
    setItems(items.filter((_, i) => i !== idx));
    if (editIndex === idx) {
      setEditIndex(null);
      setName("");
      setQuantity(0);
    }
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Inventory Management</h3>
      <form onSubmit={handleAddOrEdit}>
        <input
          type="text"
          placeholder="Item Name"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={e => setQuantity(Number(e.target.value))}
          min={1}
          required
          style={{ marginLeft: "10px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          {editIndex !== null ? "Update" : "Add"}
        </button>
        {editIndex !== null && (
          <button
            type="button"
            onClick={() => {
              setEditIndex(null);
              setName("");
              setQuantity(0);
            }}
            style={{ marginLeft: "10px" }}
          >
            Cancel
          </button>
        )}
      </form>
      <ul>
        {items.map((item, idx) => (
          <li key={idx}>
            {item.name} â€” {item.quantity}{" "}
            <button onClick={() => handleEdit(idx)}>Edit</button>
            <button onClick={() => handleDelete(idx)} style={{ marginLeft: "5px" }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default InventoryManagement;
