import React, { useState } from "react";

// Example user and activity types
interface User {
  id: number;
  name: string;
  department: string;
  role: string;
  email: string;
}

interface Activity {
  id: number;
  department: string;
  description: string;
  date: string;
}

// Example: Simulated user and activities data
const currentUser: User = {
  id: 1,
  name: "Jane Doe",
  department: "Finance",
  role: "Accountant",
  email: "jane.doe@company.com",
};

const allActivities: Activity[] = [
  { id: 1, department: "Finance", description: "Processed payroll", date: "2025-05-01" },
  { id: 2, department: "Sales", description: "Closed a major deal", date: "2025-05-02" },
  { id: 3, department: "Finance", description: "Submitted tax returns", date: "2025-05-03" },
  { id: 4, department: "HR", description: "Conducted interviews", date: "2025-05-04" },
  { id: 5, department: "Finance", description: "Updated budget forecast", date: "2025-05-05" },
];

const UserProfile: React.FC = () => {
  // In a real app, fetch user and activities from API
  const [user] = useState<User>(currentUser);

  // Filter activities to only those in the user's department
  const departmentActivities = allActivities.filter(
    activity => activity.department === user.department
  );

  return (
    <div style={{ maxWidth: 600, margin: "0 auto", padding: 24 }}>
      <h2>User Profile</h2>
      <div style={{ marginBottom: 24, border: "1px solid #eee", borderRadius: 8, padding: 16 }}>
        <strong>Name:</strong> {user.name} <br />
        <strong>Department:</strong> {user.department} <br />
        <strong>Role:</strong> {user.role} <br />
        <strong>Email:</strong> {user.email}
      </div>

      <h3>Department Activities</h3>
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 24 }}>
        <thead>
          <tr style={{ background: "#f1f3f6" }}>
            <th style={{ border: "1px solid #ddd", padding: 8 }}>Date</th>
            <th style={{ border: "1px solid #ddd", padding: 8 }}>Activity</th>
          </tr>
        </thead>
        <tbody>
          {departmentActivities.length > 0 ? (
            departmentActivities.map(activity => (
              <tr key={activity.id}>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>{activity.date}</td>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>{activity.description}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={2} style={{ textAlign: "center", padding: 16, color: "#888" }}>
                No activities found for your department.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default UserProfile;
