import React, { useState } from "react";

interface Sale {
  customer: string;
  product: string;
  quantity: number;
  amount: number;
  date: string;
}

const SalesManagement: React.FC = () => {
  const [sales, setSales] = useState<Sale[]>([]);
  const [customer, setCustomer] = useState("");
  const [product, setProduct] = useState("");
  const [quantity, setQuantity] = useState<number>(1);
  const [amount, setAmount] = useState<number>(0);
  const [date, setDate] = useState<string>("");

  const handleAddSale = (e: React.FormEvent) => {
    e.preventDefault();
    if (!customer.trim() || !product.trim() || quantity <= 0 || amount <= 0 || !date) return;
    setSales([...sales, { customer: customer.trim(), product: product.trim(), quantity, amount, date }]);
    setCustomer("");
    setProduct("");
    setQuantity(1);
    setAmount(0);
    setDate("");
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Sales Management</h3>
      <form onSubmit={handleAddSale}>
        <input
          type="text"
          placeholder="Customer Name"
          value={customer}
          onChange={e => setCustomer(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Product"
          value={product}
          onChange={e => setProduct(e.target.value)}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={e => setQuantity(Number(e.target.value))}
          min={1}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={e => setAmount(Number(e.target.value))}
          min={0}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="date"
          value={date}
          onChange={e => setDate(e.target.value)}
          required
          style={{ marginLeft: "10px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>Add Sale</button>
      </form>
      <ul>
        {sales.map((sale, idx) => (
          <li key={idx}>
            {sale.date}: {sale.customer} bought {sale.quantity} of {sale.product} for R{sale.amount}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SalesManagement;
