import React from "react";
import UserInputForm from "../UserInput/UserInputForm";
import EmployeeManagement from "../Employee/EmployeeManagement";
import InventoryManagement from "../InventoryManagement/InventoryManagement";
import OrderManagement from "../OrderManagement/OrderManagement";
import ProductionManagement from "../ProductionManagement/ProductionManagement";
import SalesManagement from "components/SalesManagement/SalesManagement";

interface AdminDashboardProps {
  darkMode: boolean;
  setDarkMode: React.Dispatch<React.SetStateAction<boolean>>;
  onLogout: () => void;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ darkMode, setDarkMode, onLogout }) => {
  const handleAdminAction = () => {
    alert("Admin-only action performed!");
  };

  return (
    <div className="dashboard-container">
      <div style={{ width: "100%", display: "flex", justifyContent: "space-between", marginBottom: 24 }}>
        <button
          onClick={() => setDarkMode(dm => !dm)}
          style={{
            padding: "10px 22px",
            borderRadius: "6px",
            background: darkMode ? "#e0e6f0" : "#2a3b4c",
            color: darkMode ? "#232a36" : "#fff",
            border: "none",
            fontWeight: 600,
            fontSize: "1rem",
            cursor: "pointer",
            transition: "background 0.2s, color 0.2s"
          }}
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
        <button
          onClick={onLogout}
          style={{
            padding: "10px 22px",
            borderRadius: "6px",
            background: "#e74c3c",
            color: "#fff",
            border: "none",
            fontWeight: 600,
            fontSize: "1rem",
            cursor: "pointer"
          }}
        >
          Logout
        </button>
      </div>
      <h2>Admin Dashboard</h2>
      <p style={{ marginBottom: 32, fontSize: "1.2rem", color: darkMode ? "#e0e6f0" : "#444" }}>
        Welcome, Admin! Here you can manage users and view all system data.
      </p>
      <button
        onClick={handleAdminAction}
        style={{
          margin: "0 0 32px 0",
          padding: "12px 28px",
          borderRadius: "6px",
          background: "#2a3b4c",
          color: "#fff",
          border: "none",
          fontWeight: 600,
          fontSize: "1.1rem",
          cursor: "pointer",
          alignSelf: "center"
        }}
      >
        Admin Only Action
      </button>
      <UserInputForm />
      <EmployeeManagement />
      <InventoryManagement />
      <OrderManagement />
      <ProductionManagement />
      <SalesManagement />
    </div>
  );
};

export default AdminDashboard;
