import React, { useState } from "react";
import UserInputForm from "../UserInput/UserInputForm";
import EmployeeManagement from "../Employee/EmployeeManagement";
import InventoryManagement from "../InventoryManagement/InventoryManagement";
import OrderManagement from "../OrderManagement/OrderManagement";
import ProductionManagement from "../ProductionManagement/ProductionManagement";
import SalesManagement from "components/SalesManagement/SalesManagement";

const AdminDashboard: React.FC = () => {
  const [darkMode, setDarkMode] = useState(false);

  const handleAdminAction = () => {
    alert("Admin-only action performed!");
  };

  // Toggle dark mode class on body
  React.useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  return (
    <div className="dashboard-container">
      <div style={{ width: "100%", display: "flex", justifyContent: "flex-end" }}>
        <button
          onClick={() => setDarkMode(dm => !dm)}
          style={{
            marginBottom: 24,
            padding: "10px 22px",
            borderRadius: "6px",
            background: darkMode ? "#e0e6f0" : "#2a3b4c",
            color: darkMode ? "#232a36" : "#fff",
            border: "none",
            fontWeight: 600,
            fontSize: "1rem",
            cursor: "pointer",
            transition: "background 0.2s, color 0.2s"
          }}
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </div>
      <h2>Admin Dashboard</h2>
      <p style={{ marginBottom: 32, fontSize: "1.2rem", color: darkMode ? "#e0e6f0" : "#444" }}>
        Welcome, Admin! Here you can manage users and view all system data.
      </p>
      <button
        onClick={handleAdminAction}
        style={{
          margin: "0 0 32px 0",
          padding: "12px 28px",
          borderRadius: "6px",
          background: "#2a3b4c",
          color: "#fff",
          border: "none",
          fontWeight: 600,
          fontSize: "1.1rem",
          cursor: "pointer",
          alignSelf: "center"
        }}
      >
        Admin Only Action
      </button>
      <UserInputForm />
      <EmployeeManagement />
      <InventoryManagement />
      <OrderManagement />
      <ProductionManagement />
      <SalesManagement />
    </div>
  );
};

export default AdminDashboard;
