import React, { useState } from "react";

interface Order {
  customer: string;
  product: string;
  quantity: number;
}

const OrderManagement: React.FC = () => {
  const [orders, setOrders] = useState<Order[]>([]);
  const [customer, setCustomer] = useState("");
  const [product, setProduct] = useState("");
  const [quantity, setQuantity] = useState<number>(1);

  const handleAddOrder = (e: React.FormEvent) => {
    e.preventDefault();
    if (!customer.trim() || !product.trim() || quantity <= 0) return;
    setOrders([...orders, { customer: customer.trim(), product: product.trim(), quantity }]);
    setCustomer("");
    setProduct("");
    setQuantity(1);
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Order Management</h3>
      <form onSubmit={handleAddOrder}>
        <input
          type="text"
          placeholder="Customer Name"
          value={customer}
          onChange={e => setCustomer(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Product"
          value={product}
          onChange={e => setProduct(e.target.value)}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={e => setQuantity(Number(e.target.value))}
          min={1}
          required
          style={{ marginLeft: "10px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>Add Order</button>
      </form>
      <ul>
        {orders.map((order, idx) => (
          <li key={idx}>
            {order.customer} ordered {order.quantity} of {order.product}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OrderManagement;
