import React, { useState } from "react";

interface Employee {
  id: number;
  employeeId: string;
  name: string;
  department: string;
  salary: number;
  assignedJob: string;
  dateOfHire: string;
}

const EmployeeManagement: React.FC = () => {
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [employeeId, setEmployeeId] = useState("");
  const [name, setName] = useState("");
  const [department, setDepartment] = useState("");
  const [salary, setSalary] = useState<number>(0);
  const [assignedJob, setAssignedJob] = useState("");
  const [dateOfHire, setDateOfHire] = useState("");
  const [editId, setEditId] = useState<number | null>(null);

  const handleAddOrUpdate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!employeeId || !name || !department || !salary || !assignedJob || !dateOfHire) return;
    if (editId !== null) {
      setEmployees(employees.map(emp =>
        emp.id === editId
          ? { ...emp, employeeId, name, department, salary, assignedJob, dateOfHire }
          : emp
      ));
      setEditId(null);
    } else {
      setEmployees([
        ...employees,
        {
          id: Date.now(),
          employeeId,
          name,
          department,
          salary,
          assignedJob,
          dateOfHire,
        },
      ]);
    }
    setEmployeeId("");
    setName("");
    setDepartment("");
    setSalary(0);
    setAssignedJob("");
    setDateOfHire("");
  };

  const handleEdit = (emp: Employee) => {
    setEditId(emp.id);
    setEmployeeId(emp.employeeId);
    setName(emp.name);
    setDepartment(emp.department);
    setSalary(emp.salary);
    setAssignedJob(emp.assignedJob);
    setDateOfHire(emp.dateOfHire);
  };

  const handleDelete = (id: number) => {
    setEmployees(employees.filter(emp => emp.id !== id));
    if (editId === id) {
      setEditId(null);
      setEmployeeId("");
      setName("");
      setDepartment("");
      setSalary(0);
      setAssignedJob("");
      setDateOfHire("");
    }
  };

  return (
    <div style={{ width: "100%" }}>
      <h3>Employee Management</h3>
      <form onSubmit={handleAddOrUpdate}>
        <input
          type="text"
          placeholder="Employee ID"
          value={employeeId}
          onChange={e => setEmployeeId(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Department"
          value={department}
          onChange={e => setDepartment(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Salary"
          value={salary}
          onChange={e => setSalary(Number(e.target.value))}
          required
        />
        <input
          type="text"
          placeholder="Assigned Job"
          value={assignedJob}
          onChange={e => setAssignedJob(e.target.value)}
          required
        />
        <input
          type="date"
          value={dateOfHire}
          onChange={e => setDateOfHire(e.target.value)}
          required
        />
        <button type="submit">{editId !== null ? "Update" : "Add"} Employee</button>
        {editId !== null && (
          <button
            type="button"
            onClick={() => {
              setEditId(null);
              setEmployeeId("");
              setName("");
              setDepartment("");
              setSalary(0);
              setAssignedJob("");
              setDateOfHire("");
            }}
            style={{ marginLeft: 10 }}
          >
            Cancel
          </button>
        )}
      </form>
      <ul>
        {employees.map(emp => (
          <li key={emp.id}>
            <strong>{emp.employeeId}</strong> | {emp.name} | {emp.department} | R{emp.salary} | {emp.assignedJob} | {emp.dateOfHire}
            <button style={{ marginLeft: 10 }} onClick={() => handleEdit(emp)}>
              Edit
            </button>
            <button style={{ marginLeft: 5 }} onClick={() => handleDelete(emp.id)}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeManagement;
