import React, { useState } from "react";
import LoginForm from "./components/Auth/LoginForm";
import AdminDashboard from "./components/Dashboard/AdminDashboard";
import HRDashboard from "./components/Dashboard/HRDashboard";
import UserDashboard from "./components/Dashboard/UserDashboard";

const App: React.FC = () => {
  const [role, setRole] = useState<string | null>(null);

  if (!role) {
    return <LoginForm onLogin={setRole} />;
  }

  return (
    <div>
      <h1>Welcome, {role}!</h1>
      {/* Show different dashboards based on role */}
      {role === "admin" && <div>Admin Dashboard (placeholder)</div>}
      {role === "hr" && <div>HR Dashboard (placeholder)</div>}
      {role === "user" && <div>User Dashboard (placeholder)</div>}
    </div>
  );
};

export default App;
