import React from "react";
import UserInputForm from "../UserInput/UserInputForm";
import EmployeeManagement from "../Employee/EmployeeManagement";
import InventoryManagement from "../InventoryManagement/InventoryManagement";
import OrderManagement from "../OrderManagement/OrderManagement";
import ProductionManagement from "../ProductionManagement/ProductionManagement";

const HRDashboard: React.FC = () => (
  <div>
     <button
        onClick={() => setDarkMode(dm => !dm)}
        style={{
          padding: "10px 22px",
          borderRadius: "6px",
          background: darkMode ? "#e0e6f0" : "#2a3b4c",
          color: darkMode ? "#232a36" : "#fff",
          border: "none",
          fontWeight: 600,
          fontSize: "1rem",
          cursor: "pointer",
          transition: "background 0.2s, color 0.2s"
        }}
      >
        {darkMode ? "Light Mode" : "Dark Mode"}
      </button>
    <h2>HR Dashboard</h2>
    <UserInputForm />
    <EmployeeManagement />
    <InventoryManagement />
    <OrderManagement />
    <ProductionManagement />
    <p>Welcome, HR! Here you can manage employee records and HR tasks.</p>
  </div>
);

export default HRDashboard;
