import React, { useState } from "react";

const UserProfile: React.FC = () => {
  const [name, setName] = useState("John Doe");
  const [email, setEmail] = useState("john@example.com");
  const [phone, setPhone] = useState("0700000000");
  const [department, setDepartment] = useState("Production");
  const [password, setPassword] = useState("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    alert("Profile updated!");
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>User Profile</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input value={name} onChange={e => setName(e.target.value)} required style={{ marginLeft: 8 }} />
        </label>
        <br /><br />
        <label>
          Email:
          <input value={email} onChange={e => setEmail(e.target.value)} required style={{ marginLeft: 8 }} />
        </label>
        <br /><br />
        <label>
          Phone:
          <input value={phone} onChange={e => setPhone(e.target.value)} required style={{ marginLeft: 8 }} />
        </label>
        <br /><br />
        <label>
          Department:
          <input value={department} onChange={e => setDepartment(e.target.value)} required style={{ marginLeft: 8 }} />
        </label>
        <br /><br />
        <label>
          Password:
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} required style={{ marginLeft: 8 }} />
        </label>
        <br /><br />
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default UserProfile;
