import React from "react";
import UserInputForm from "../UserInput/UserInputForm";
import EmployeeManagement from "../Employee/EmployeeManagement";
import InventoryManagement from "../InventoryManagement/InventoryManagement";
import OrderManagement from "../OrderManagement/OrderManagement";
import ProductionManagement from "../ProductionManagement/ProductionManagement";
import SalesManagement from "components/SalesManagement/SalesManagement";

const AdminDashboard: React.FC = () => {
  const handleAdminAction = () => {
    alert("Admin-only action performed!");
  };

  return (
    <div className="dashboard-container">
      <h2>Admin Dashboard</h2>
      <p style={{ marginBottom: 32, fontSize: "1.2rem", color: "#444" }}>
        Welcome, Admin! Here you can manage users and view all system data.
      </p>
      <button
        onClick={handleAdminAction}
        style={{
          margin: "0 0 32px 0",
          padding: "12px 28px",
          borderRadius: "6px",
          background: "#2a3b4c",
          color: "#fff",
          border: "none",
          fontWeight: 600,
          fontSize: "1.1rem",
          cursor: "pointer",
          alignSelf: "center"
        }}
      >
        Admin Only Action
      </button>
      <UserInputForm />
      <EmployeeManagement />
      <InventoryManagement />
      <OrderManagement />
      <ProductionManagement />
      <SalesManagement />
    </div>
  );
};

export default AdminDashboard;
