import React, { useState } from "react";

interface Order {
  customer: string;
  product: string;
  quantity: number;
}

const OrderManagement: React.FC = () => {
  const [orders, setOrders] = useState<Order[]>([]);
  const [customer, setCustomer] = useState("");
  const [product, setProduct] = useState("");
  const [quantity, setQuantity] = useState<number>(1);
  const [editIndex, setEditIndex] = useState<number | null>(null);

  const handleAddOrEdit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!customer.trim() || !product.trim() || quantity <= 0) return;

    if (editIndex !== null) {
      // Edit existing order
      const updated = [...orders];
      updated[editIndex] = { customer: customer.trim(), product: product.trim(), quantity };
      setOrders(updated);
      setEditIndex(null);
    } else {
      // Add new order
      setOrders([...orders, { customer: customer.trim(), product: product.trim(), quantity }]);
    }
    setCustomer("");
    setProduct("");
    setQuantity(1);
  };

  const handleEdit = (idx: number) => {
    setEditIndex(idx);
    setCustomer(orders[idx].customer);
    setProduct(orders[idx].product);
    setQuantity(orders[idx].quantity);
  };

  const handleDelete = (idx: number) => {
    setOrders(orders.filter((_, i) => i !== idx));
    if (editIndex === idx) {
      setEditIndex(null);
      setCustomer("");
      setProduct("");
      setQuantity(1);
    }
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Order Management</h3>
      <form onSubmit={handleAddOrEdit}>
        <input
          type="text"
          placeholder="Customer Name"
          value={customer}
          onChange={e => setCustomer(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Product"
          value={product}
          onChange={e => setProduct(e.target.value)}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={e => setQuantity(Number(e.target.value))}
          min={1}
          required
          style={{ marginLeft: "10px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          {editIndex !== null ? "Update" : "Add"}
        </button>
        {editIndex !== null && (
          <button
            type="button"
            onClick={() => {
              setEditIndex(null);
              setCustomer("");
              setProduct("");
              setQuantity(1);
            }}
            style={{ marginLeft: "10px" }}
          >
            Cancel
          </button>
        )}
      </form>
      <ul>
        <p>Total Orders: {orders.length}</p>
        {orders.map((order, idx) => (
            
          <li key={idx}>
            {order.customer} ordered {order.quantity} of {order.product}
            <button onClick={() => handleEdit(idx)} style={{ marginLeft: "10px" }}>
              Edit
            </button>
            <button onClick={() => handleDelete(idx)} style={{ marginLeft: "5px" }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OrderManagement;
