import React from "react";
import UserInputForm from "../UserInput/UserInputForm";
import EmployeeManagement from "../Employee/EmployeeManagement";
import InventoryManagement from "../InventoryManagement/InventoryManagement";
import OrderManagement from "components/OrderManagement/OrderManagement";

const AdminDashboard: React.FC = () => {
  const handleAdminAction = () => {
    alert("Admin-only action performed!");
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <UserInputForm />
      <button onClick={handleAdminAction} style={{ margin: "10px 0" }}>
        Admin Only Action
      </button>
      <EmployeeManagement />
      <InventoryManagement />
      <OrderManagement />
      <p>Welcome, Admin! Here you can manage users and view all system data.</p>
    </div>
  );
};

export default AdminDashboard;
